mapper.insert:
INSERT INTO orders (ORD_CODE,B_CODE,ORD_NAME,ORD_PHONE,SEND_NAME,SEND_PHONE,ORD_POSTCODE,ORD_ADDR,ORD_DETAIL,ORD_MEMO,ORD_DATE,ORD_PAY,ORD_SUM, ORD_FEE)
VALUES(#{ordCode},#{bCode},
(SELECT b_name FROM buyer WHERE b_code = #{bCode}),
(SELECT b_phone FROM buyer WHERE b_code = #{bCode})
, #{sendName}
, #{sendPhone}
, #{ordPostcode}
, #{ordAddr}
, #{ordDetail}
, #{ordMemo}
, sysdate
, #{ordPay}
, #{ordSum}
, #{ordFee})

mapper.select:
SELECT * FROM ( SELECT rownum no, PRD.* FROM ( SELECT C_CODE, S_CODE, c.PRD_CODE, C_CNT, PRD_NAME, PRICE, PRD_FEE, stored_name FROM cart c, prd p, prd_file pf
AND c.PRD_CODE =p.PRD_CODE AND p.PRD_CODE = pf.PRD_CODE(+) AND B_CODE = #{user}
ORDER BY C_CODE ) PRD )
AND no BETWEEN #{startNo } AND #{endNo }

SELECT C_CODE, S_CODE, c.PRD_CODE, C_CNT, PRD_NAME, PRICE, PRD_FEE, stored_name FROM cart c, prd p, prd_file pf
AND c.PRD_CODE = p.PRD_CODE AND p.PRD_CODE = pf.PRD_CODE AND C_CODE = #{cCode}
ORDER BY C_CODE

SELECT adr_name, adr_phone, adr_postcode, adr_addr, adr_detail FROM buyer_adr
AND b_code = #{bCode}
ORDER BY ADR_CHK DESC

SELECT * FROM orders
WHERE ORD_CODE = #{ordCode}
ORDER BY ORD_CODE

mapper.update:
	UPDATE prd
    SET prd_cnt = prd_cnt-#{cCnt}
    WHERE prd_code = #{prdCode}

UPDATE cart
SET c_cnt = #{cCnt}

	AND c_code = #{cCode}

UPDATE Buyer
SET B_Pw = #{bPw }
WHERE B_Id = #{bId }

UPDATE Buyer SET
B_Pw = #{bPw },
B_Name = #{bName }, B_Phone = #{bPhone }, B_Email = #{bEmail }, Ad_Sms = #{adSms }, Ad_Email = #{adEmail } WHERE B_Code = #{bCode }

UPDATE Buyer_Prof
SET 
	Origin_Name = #{originName }, 
	Stored_Name = #{storedName }
WHERE b_code = #{bCode }

UPDATE Cmp_File
SET
	Origin_Name = #{originName }, 
	Stored_Name = #{storedName }
WHERE Cmp_No = #{cmpNo }

mapper.select:
 	SELECT count(*)
 	FROM Buyer_Adr
		WHERE b_Code = #{bCode }

mapper.insert:
INSERT INTO Buyer_Adr (
	Adr_Code, B_Code, Adr_Name, Adr_Phone, Adr_Postcode, Adr_Addr, Adr_Detail, Adr_Chk
) VALUES (
	#{adrCode }, #{bCode }, #{adrName }, #{adrPhone }, #{adrPostcode }, #{adrAddr }, #{adrDetail }, #{adrChk }
)

mapper.update:
 	UPDATE Buyer_Adr
 	SET Adr_Chk = 'N' 
		WHERE B_Code = #{bCode }

UPDATE Buyer_Adr
SET Adr_Chk = 'Y'

	AND Adr_Code = #{adrCode }
	AND B_Code = #{bCode }

mapper.select:
 	SELECT *
 	FROM Buyer_Adr
		WHERE Adr_Code = #{adrCode }

SELECT count(*) FROM cart c, prd p, prd_file pf
AND c.PRD_CODE =p.PRD_CODE AND p.PRD_CODE = pf.PRD_CODE(+) AND B_CODE = #{user}
ORDER BY C_CODE DESC

