mapper.select:
SELECT * FROM (
	SELECT rownum no, RVW.*  FROM (
		SELECT * FROM (
		    SELECT '체험단' ctg, rvw_code code, b_code bcode, exp_code rvw, rvw_grade grade, rvw_content content, rvw_date entdate FROM exp_review
		    UNION ALL
		    SELECT '새활용' ctg, upcy_code code, b_code bcode, prd_code rvw, upcy_grade grade, upcy_content content, upcy_date entdate FROM upcy_review
		)

			AND bcode = #{user }
			AND content LIKE '%' || #{search } || '%'

		ORDER BY entdate
	) RVW
)

	AND no BETWEEN #{startNo } AND #{endNo }

SELECT count(*) FROM (
	SELECT * FROM (
	    SELECT '체험단' ctg, rvw_code code, b_code bcode, exp_code rvw, rvw_grade grade, rvw_content content, rvw_date entdate FROM exp_review
	    UNION ALL
	    SELECT '새활용' ctg, upcy_code code, b_code bcode, prd_code rvw, upcy_grade grade, upcy_content content, upcy_date entdate FROM upcy_review
	)
)

	AND bcode = #{user }
	AND content LIKE '%' || #{search } || '%'

ORDER BY entdate

SELECT count(*) FROM ( SELECT rownum no, OD.* FROM ( SELECT O.ord_date entdate, OD.title title, OD.cnt cnt, OD.price price, OD.code code, OD.detail detail FROM orders O, ( SELECT OD.ord_name title, OD.ord_cnt cnt, OD.ord_price price, P.prd_code code, P.prd_detail detail, OD.ord_code ord_code FROM order_detail OD, prd P
AND OD.prd_code = P.prd_code(+) AND OD.stt_no = 940 AND P.prd_detail LIKE '%' || #{search } || '%'
) OD
AND O.ord_code(+) = OD.ord_code AND O.b_code = #{user }
ORDER BY O.ord_date ) OD UNION ALL SELECT rownum no, EXP.* FROM ( SELECT ER.res_date entdate, ER.res_expname title, ER.res_cnt cnt, ER.res_price price, ES.exp_code code, ES.exp_detail detail FROM exp_res ER, ( SELECT E.exp_code, E.exp_name, E.exp_detail, ES.sch_no FROM exp E, exp_sch ES
AND E.exp_code = ES.exp_code(+)
) ES
AND ER.sch_no(+) = ES.sch_no AND res_date < sysdate AND res_cnf = 'N' AND ER.b_code = #{user } AND ( ER.res_expname LIKE '%' || #{search } || '%' OR ES.exp_detail LIKE '%' || #{search } || '%' )
ORDER BY res_date ) EXP )

SELECT * FROM (
    SELECT rownum no, OD.* FROM (
    SELECT O.ord_date entdate, OD.title title, OD.cnt cnt, OD.price price, OD.code code, OD.detail detail FROM orders O, (
        SELECT OD.ord_name title, OD.ord_cnt cnt, OD.ord_price price, P.prd_code code, P.prd_detail detail, OD.ord_code ord_code FROM order_detail OD, prd P

	        AND OD.prd_code = P.prd_code(+)
	        AND OD.stt_no = 940
	        AND P.prd_detail LIKE '%' || #{search } || '%'

    ) OD

	    AND O.ord_code(+) = OD.ord_code
	    AND O.b_code = #{user }

    ORDER BY O.ord_date
    ) OD
    UNION ALL
    SELECT rownum no, EXP.* FROM (
        SELECT ER.res_date entdate, ER.res_expname title, ER.res_cnt cnt, ER.res_price price, ES.exp_code code, ES.exp_detail detail FROM exp_res ER, (
            SELECT E.exp_code, E.exp_name, E.exp_detail, ES.sch_no FROM exp E, exp_sch ES
            WHERE E.exp_code = ES.exp_code(+)
        ) ES

	        AND ER.sch_no(+) = ES.sch_no 
            AND res_date < sysdate
            AND res_cnf = 'N'
            AND ER.b_code = #{user }
            AND (
                ER.res_expname LIKE '%' || #{search } || '%'
                OR ES.exp_detail LIKE '%' || #{search } || '%'
            )

        ORDER BY res_date
    ) EXP
)

	AND no BETWEEN #{startNo } AND #{endNo }
