mapper.insert:

	SELECT ('MGR'||LPAD(manager_seq.nextval, 7, '0'))FROM dual

INSERT INTO manager
(
	mgr_code, deptno
	, mgr_name, mgr_phone
	, mgr_email, mgr_birth
	, mgr_gender, mgr_entDate
)
VALUES
(	
	#{mgrCode }, #{deptno }
	, #{mgrName }, #{mgrPhone }
	, #{mgrEmail }, #{mgrBirth }
	, #{mgrGender }, #{mgrEntDate }
)


    SELECT MGR_FILE_SEQ.nextval FROM dual

INSERT INTO Mgr_File 
	(mgr_flNo, mgr_code, ct_MflNo, origin_name, stored_name)
VALUES
	(#{mgrFlNo}, #{mgrCode}, #{ctMflNo}, #{originName}, #{storedName}
	)

mapper.update:
UPDATE Manager SET
mgr_code = #{mgrCode }
, deptno = #{deptno }
, mgr_name = #{mgrName }
, mgr_phone = #{mgrPhone }
, mgr_email = #{mgrEmail }
mgr_code = #{mgrCode}

UPDATE mgr_file SET
	origin_name = #{originName } 
	, stored_name = #{storedName }

	AND mgr_code = #{mgrCode }
	AND mgr_flno = #{mgrFlNo }
