mapper.insert:

    SELECT ('EXP'||LPAD(exp_seq.nextval, 7, '0')) FROM dual

INSERT INTO exp
( exp_code
, s_code
, exp_name
, exp_price
, exp_detail
, exp_hit
, exp_date
)VALUES
( #{expCode }
, #{sCode }
, #{expName }
, #{expPrice }
, #{expDetail }
, #{expHit }
, default
)


    SELECT EXP_FILE_SEQ.nextval FROM dual

INSERT INTO Exp_File 
	(exp_flNo
	, exp_code
	, ct_pflNo
	, origin_name
 	, stored_name 
 	)
VALUES
	(#{expFlNo}
	, #{expCode}
	, #{ctPflNo}
	, #{originName}
	, #{storedName}
	)


    SELECT EXP_SCH_SEQ.nextval FROM dual

INSERT INTO Exp_Sch
	(sch_no, exp_code
	, sch_date , sch_time
 	, sch_cnt
 	)
VALUES
	(#{schNo}, #{expCode}
	, #{schDate}, #{schTime}
	, #{schCnt}
	)

mapper.update:
	UPDATE Exp SET
    exp_code = #{expCode }
    , exp_name = #{expName }
    , exp_price = #{expPrice }
    , exp_detail = #{expDetail }
    , exp_date = default
    exp_code = #{expCode }

UPDATE exp_res SET
  res_name = #{resName}
  , res_phone = #{resPhone}
  , res_email = #{resEmail}
  , res_cnt = #{resCnt }
  , res_time = #{resTime }
  , res_date = sysdate
  res_code = #{resCode }

select.where:
AND r.res_code = #{resCode}
AND s.sch_no = #{schNo}
AND r.resCnt = #{resCnt}

AND r.res_code = #{resCode}
AND s.sch_no = #{schNo}
AND s.schCnt = #{schCnt}

mapper.update:
UPDATE exp_sch SET
s.sch_no = #{schNo}

AND s.schCnt = #{schCnt}
