mapper.update:
UPDATE order_detail
SET stt_no = #{sttNo}

	AND orddt_code = #{orddtCode}

mapper.select:
SELECT * FROM (
	SELECT rownum rnum, E.* FROM (


			AND exp_name LIKE '%' || #{search } || '%' 
				OR exp_code LIKE '%' || #{search } || '%' 

		ORDER BY exp_code
	) E
)

	AND rnum BETWEEN #{startNo} AND #{endNo}

SELECT COUNT(*) FROM (
	SELECT * FROM exp_res

		AND sch_no = #{schNo}

)
