mapper.select:
	SELECT *
	FROM (
		SELECT rownum no, PRD.* FROM (
			SELECT * FROM prd

				AND s_code = #{user }
				AND ct_pno = 0
				AND prd_out = 'N'
				AND (
				prd_name LIKE '%' || #{search } || '%'
				OR prd_detail LIKE '%' || #{search } || '%'
				)

			ORDER BY prd_code DESC
	) PRD
)

	AND no BETWEEN #{startNo } AND #{endNo }

SELECT * FROM ( SELECT rownum no, ORD.* FROM ( SELECT od.ORDDT_CODE, od.ORD_CODE, od.PRD_CODE, od.ORD_NAME, o.SEND_NAME , o.SEND_PHONE, o.ORD_POSTCODE, o.ORD_ADDR, o.ORD_DETAIL, o.ORD_MEMO , ORD_PRICE, ORD_CNT, od.ORD_SUM, STT_NO, ORD_DATE, SHIP_NAME, SHIP_NO FROM order_detail od, orders o, ship s, prd p
AND od.ord_code = o.ord_code AND od.orddt_code = s.orddt_code(+) AND od.prd_code = p.prd_code AND p.ct_pno = 1 AND p.s_code = #{user }
ORDER BY od.ORDDT_CODE DESC ) ORD )
AND od.ord_code = o.ord_code AND od.orddt_code = s.orddt_code(+) AND od.prd_code = #{prdCode}
ORDER BY od.ORDDT_CODE DESC

mapper.update:
UPDATE order_detail
SET stt_no = #{sttNo}

	AND orddt_code = #{orddtCode}

mapper.select:
SELECT * FROM (
	SELECT rownum no, exp.* FROM (
		SELECT e.exp_code, e.s_code, e.exp_name, e.exp_price, e.exp_detail, e.exp_hit, e.exp_date
		FROM 
			exp e, seller s

			AND s.s_code = e.s_code
			AND s.s_code = #{user}
			AND (
				exp_name LIKE '%' || #{search } || '%' 
				OR exp_code LIKE '%' || #{search } || '%' 
				)

		ORDER BY exp_code DESC
	) exp
)

	AND no BETWEEN #{startNo } AND #{endNo }

SELECT count(*) FROM exp_Sch
AND ( exp_code LIKE '%' || #{search} || '%' AND sch_no LIKE '%' || #{search } || '%' ) AND exp_code = #{user }
ORDER BY sch_no DESC

SELECT count(*) FROM (
    SELECT rownum no, P.* FROM (
        SELECT * FROM prd

			AND s_code = #{user }
			AND (
				prd_name LIKE '%' || #{search } || '%'
				OR prd_detail LIKE '%' || #{search } || '%'
			)

        ORDER BY prd_date
    ) P
)

SELECT count(*) FROM (
	SELECT rownum no, ODD.* FROM (
	    SELECT O.ord_code ordCode, OD.orddt_code orddtCode, OD.stt_no sttNo, OD.prd_code prdCode, O.ord_date ordDate, O.ord_name ordName, O.ord_phone ordPhone, OD.ord_sum ordSum, OD.price price, OD.prd_name prdName
	    FROM orders O, (
	        SELECT P.prd_code prdCode, P.ct_pno ctPno, P.s_code sCode, OD.ord_code ord_code, OD.orddt_code orddt_code, OD.stt_no stt_no, OD.ord_sum ord_sum, P.prd_name prd_name, P.prd_code prd_code, P.price price
	        FROM prd P, order_detail OD

		        AND P.prd_code = OD.prd_code(+)
		        AND P.s_code = #{user }
		        AND (
		        	P.prd_name LIKE '%' || #{search } || '%'
		        	OR P.prd_detail LIKE '%' || #{search } || '%'
		        )

	    ) OD

	    	AND O.ord_code = OD.ord_code(+)
	    	AND sCode = #{user }

	    ORDER BY O.ord_date
	) ODD
)

SELECT * FROM (
    SELECT rownum no, P.* FROM (
        SELECT * FROM prd

			AND s_code = #{user }
			AND (
				prd_name LIKE '%' || #{search } || '%'
				OR prd_detail LIKE '%' || #{search } || '%'
			)

        ORDER BY prd_date
    ) P
)

	AND no BETWEEN #{startNo } AND #{endNo }

SELECT * FROM (
	SELECT rownum no, ODD.* FROM (
	    SELECT O.ord_code ordCode, OD.orddt_code orddtCode, OD.stt_no sttNo, OD.prd_code prdCode, O.ord_date ordDate, O.ord_name ordName, O.ord_phone ordPhone, OD.ord_sum ordSum, OD.price price, OD.prd_name prdName 
	    FROM orders O, (
	        SELECT P.prd_code prdCode, P.ct_pno ctPno, P.s_code sCode, OD.ord_code ord_code, OD.orddt_code orddt_code, OD.stt_no stt_no, OD.ord_sum ord_sum, P.prd_name prd_name, P.prd_code prd_code, P.price price 
	        FROM prd P, order_detail OD

		        AND P.prd_code = OD.prd_code(+)
		        AND P.s_code = #{user }
		        AND (
		        	P.prd_name LIKE '%' || #{search } || '%'
		        	OR P.prd_detail LIKE '%' || #{search } || '%'
		        )

	    ) OD

		    AND O.ord_code = OD.ord_code(+)
		    AND sCode = #{user }

	    ORDER BY O.ord_date
	) ODD
)

	AND no BETWEEN #{startNo } AND #{endNo }
