mapper.select:
			SELECT * FROM (
        SELECT rownum AS no, F.* FROM (
            SELECT F.*, C.ct_faqname
            FROM Faq F
            JOIN Faq_ct C ON F.ct_faqno = C.ct_faqno
            ORDER BY F.faq_no
        ) F
    )

    	AND no BETWEEN #{startNo} AND #{endNo}

mapper.update:
UPDATE Notice
	SET ntc_hit = ntc_hit + 1

	AND ntc_code = #{ntcCode }

UPDATE Oto
	SET oto_hit = oto_hit + 1

	AND oto_code = #{otoCode}
