mapper.select:
SELECT * FROM (
	SELECT rownum rnum, B.* FROM (

		ORDER BY oto_code DESC
	) B
)

	AND rnum BETWEEN #{startNo } AND #{endNo }

SELECT * FROM (
	SELECT rownum rnum, B.* FROM (

		ORDER BY b_code DESC
	) B
)

	AND rnum BETWEEN #{startNo } AND #{endNo }

SELECT ANS_CODE, OTO_CODE, MGR_CODE, ANS_CONTENT, ANS_DATE FROM ANS
OTO_CODE = #{otoCode }
ORDER BY ANS_DATE DESC

mapper.update:
UPDATE Buyer SET
, b_ct_code = #{bCtCode }
, b_name = #{bName }
, rank_no = #{rankNo }


b_code = #{bCode }

UPDATE buyer_prof SET
	origin_name = #{originName } 
	, stored_name = #{storedName }

	AND b_code = #{mgrCode }
	AND b_profno = #{bProfNo }
