mapper.select:
SELECT * FROM (
	SELECT rownum rnum, B.* FROM (

		ORDER BY oto_code DESC
	) B
)

	AND rnum BETWEEN #{startNo } AND #{endNo }

SELECT * FROM (
	SELECT rownum rnum, B.* FROM (

		ORDER BY b_code DESC
	) B
)

	AND rnum BETWEEN #{startNo } AND #{endNo }

mapper.update:
UPDATE BUYER
SET b_name = #{bName },
	b_phone = #{bPhone },
	b_email = #{bEmail }

	AND b_code = #{bCode }
