mapper.select:
SELECT mgr_code, deptno, mgr_id, mgr_pw, mgr_out 
FROM manager

	AND mgr_id = #{username }

SELECT count(*)
FROM manager

	AND mgr_code = #{mgrCode }
	AND mgr_name = #{mgrName }
	AND mgr_phone = #{mgrPhone }
	AND mgr_email = #{mgrEmail }
	AND mgr_gender = #{mgrGender }
	AND TO_CHAR(mgr_birth, 'YYYY-MM-DD') = TO_DATE(#{mgrBirth }, 'YYYY-MM-DD')
	AND mgr_id IS NULL
	AND mgr_pw IS NULL

mapper.update:
UPDATE manager
SET mgr_id = #{mgrId }, mgr_pw = #{mgrPw }

	mgr_code = #{mgrCode }
