mapper.insert:

    SELECT ('EXP'||LPAD(exp_seq.nextval, 7, '0')) FROM dual

INSERT INTO exp
( exp_code
, s_code
, exp_name
, exp_price
, exp_detail
, exp_hit
, exp_date
)VALUES
( #{expCode }
, #{sCode }
, #{expName }
, #{expPrice }
, #{expDetail }
, #{expHit }
, default
)


    SELECT EXP_FILE_SEQ.nextval FROM dual

INSERT INTO Exp_File 
	(exp_flNo
	, exp_code
	, ct_pflNo
	, origin_name
 	, stored_name 
 	)
VALUES
	(#{expFlNo}
	, #{expCode}
	, #{ctPflNo}
	, #{originName}
	, #{storedName}
	)


    SELECT EXP_SCH_SEQ.nextval FROM dual

INSERT INTO Exp_Sch
	(sch_no, exp_code
	, sch_date , sch_time
 	, sch_cnt
 	)
VALUES
	(#{schNo}, #{expCode}
	, #{schDate}, #{schTime}
	, #{schCnt}
	)
